mapper.select:

select
t.id,t.attendence_id,t.course_id,status,count,user_id,state
from
t_attendence_detail t, t_attendence d
where t.attendence_id =
d.attendence_id
and t.course_id = d.course_id
and t.course_id = #{0}
and user_id = #{1}
and state = #{2}

mapper.update:
update t_attendence_detail
set state = #{2}
where
attendence_id = #{0} AND user_id = #{1}

mapper.select:
select
attendence_id,course_id,status,count
from t_attendence
where course_id = #{0} 
and status = #{1}
